import React, { useState } from 'react';

function SearchBar({ onSearch }) {
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [priceRange, setPriceRange] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Build the params object
    const params = {};
    if (name) params.name = name;
    if (category) params.category = category;
    
    // Price range logic
    if (priceRange === '0-2') params.priceRange = '0-2';
    if (priceRange === '2-5') params.priceRange = '2-5';
    if (priceRange === '5+') params.priceRange = '5-999';

    onSearch(params);
  };

  const handleClear = () => {
    setName('');
    setCategory('');
    setPriceRange('');
    onSearch({}); // Perform an empty search to reset
  };

  return (
    <form onSubmit={handleSubmit} className="search-bar">
      <input
        type="text"
        placeholder="Search by name..."
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option value="">All Categories</option>
        <option value="Classic">Classic</option>
        <option value="Premium">Premium</option>
        <option value="Festive">Festive</option>
        {/* Add more categories as needed */}
      </select>
      <select value={priceRange} onChange={(e) => setPriceRange(e.target.value)}>
        <option value="">All Prices</option>
        <option value="0-2">$0 - $2</option>
        <option value="2-5">$2 - $5</option>
        <option value="5+">$5+</option>
      </select>
      <button type="submit">Search</button>
      <button type="button" onClick={handleClear} className="clear-button">Clear</button>
    </form>
  );
}

export default SearchBar;
